            "name": "admin",
            "tenantName": "admin",
            "tenantId": "222"
        },
        {
            "id": "2",
            "name": "KeystoneAdmin",
            "tenantName": "admin",
            "tenantId": "222"
        },
        {
            "id": "3",
            "name": "KeystoneServiceAdmin",
            "tenantName": "admin",
            "tenantId": "222"
        }
    ]
}

